<template>
  <el-breadcrumb separator="/" class="breadcrumb-block" >
    <template v-if="menus && menus.length > 0" v-for="(bread, index) in menus">
      <el-breadcrumb-item v-if="index < menus.length - 1" :key="'bread_' + index">{{bread.name}}</el-breadcrumb-item>
      <el-breadcrumb-item v-else :key="'bread_' + index">{{bread.name}}</el-breadcrumb-item>
    </template>
    <template v-if="!menus || menus.length === 0">
      <el-breadcrumb-item>首页</el-breadcrumb-item>
    </template>
  </el-breadcrumb>
</template>
<script>
  export default {
    name: 'Breadcrumb',
    props: {
      breads: {
        type: Array
      }
    },
    computed: {
      menus () {
        return this.breads;
      }
    }
  };
</script>
<style lang="stylus" scoped>
  .breadcrumb-block
    padding: 15px 0 10px 30px
    background: #fff
    height: 41px
    margin: 2px 0
</style>
