<template>
  <span class="item">
    <i v-if="node.data.type === 'menu'" class="icon-folder"></i>
    <i v-if="node.data.type === 'func'" class="icon-sticky-note"></i>
    {{node.label}}
    <span class="btn-block" v-if="type && type === 1">
      <i @click.stop="plus(node)" class="icon-plus" v-if="node.data.type === 'menu'"></i>
      <i @click.stop="trash(node)" class="icon-trash-empty"></i>
    </span>
  </span>
</template>
<script>
  export default {
    name: 'menuTreeItem',
    props: {
      node: {
        type: Object
      },
      type: {
        type: Number
      }
    },
    methods: {
      plus (node) {
        this.$emit('plus', node);
      },
      trash (node) {
        this.$emit('trash', node);
      },
      sort (node) {
        this.$emit('sort', node);
      }
    }
  };
</script>
<style lang="stylus" scoped>
  .item
    display:inline-block
    width: 90%
    .btn-block
      float: right
      display: none
      i
        padding: 2px 6px
        border: 1px solid #ccc
  .item:hover
    .btn-block
      display: inline-block
</style>

