import Vue from 'vue';
import Router from 'vue-router';

import login from '../pages/login';
import bsmp from '../pages/bsmp';

/** 扶뒬못 begin**/
import index from '../pages/index';
/** 扶뒬못 end**/

import views from '@/views';

Vue.use(Router);

export default new Router({
  routes: [
    {
      path: '/login',
      name: 'login',
      component: login
    },
    {
      path: '/',
      name: 'bsmp',
      component: bsmp,
      children: [
        {
          path: '/index',
          name: 'index',
          component: index
        }, ...views]
    }
  ]
});
