import * as types from '../mutation-types';

const state = {
  params: null, // 参数
  companies: [], // 公司栏列表
  company: null
};

const mutations = {
  /**
   * 获取公司
   * @param state
   * @param params
   */
  [types.GET_COMPANIES] (state, params) {
    state.params = params;
  },

  /**
   * 接受公司列表
   * @param state
   * @param error
   * @param data
   */
  [types.RECEIVE_COMPANIES] (state, {error, data}) {
    state.menus = [];
    if (!error) {
      if (data.code === '0') {
        state.companies = data.data;
      }
    }
  },
  /**
   * 当前切换公司
   * @param state
   * @param data
   */
  [types.SWITCH_COMPANY] (state, data) {
    state.company = data;
  }
};

export default {
  state,
  mutations
};
