<template>
  <div>
    <query-panel>
      <el-form :inline="true" :model="search" class="demo-form-inline">
        <el-form-item>
          <el-input v-model="search.code" placeholder="字典代码"></el-input>
        </el-form-item>
        <el-form-item>
          <el-input v-model="search.name" placeholder="字典名称"></el-input>
        </el-form-item>
        <el-form-item>
          <el-select clearable v-model="search.status" placeholder="状态">
            <el-option v-for="(val, key) in statusMap" :label="val" :value="key"
                       :key="'search-status-'+ key"></el-option>
          </el-select>
        </el-form-item>
        <el-form-item>
          <el-button v-show="optAuth.search" type="primary" @click="query">查询</el-button>
        </el-form-item>
      </el-form>
    </query-panel>
    <el-row class="toolbar">
      <el-button v-show="optAuth.add" @click="showDialog(1)" type="primary">新增字典分组</el-button>
      <el-button v-show="optAuth.search" @click="refresh"><i class="icon-arrows-cw"></i>&nbsp;&nbsp;刷新</el-button>
    </el-row>
    <el-table
      highlight-current-row
      v-loading.body="loading"
      :data="table.items"
      @row-click="selectRow"
      @expand="expand"
      :max-height="height - 165"
      border>
      <el-table-column type="expand">
        <template slot-scope="props">
          <el-table
            highlight-current-row
            v-loading.body="props.row.loading"
            :data="props.row.nodes"
            border>
            <el-table-column
              prop="code"
              label="字典代码"
              min-width="150">
            </el-table-column>
            <el-table-column
              prop="name"
              label="字典名称"
              min-width="100">
            </el-table-column>
            <el-table-column
              prop="value"
              label="参数值"
              min-width="100">
            </el-table-column>
            <el-table-column
              prop="companyId"
              label="公司"
              min-width="200">
            </el-table-column>
            <el-table-column
              prop="sort"
              label="排序"
              min-width="100">
            </el-table-column>
            <el-table-column
              prop="status"
              label="状态"
              :formatter="status"
              min-width="100">
            </el-table-column>
            <el-table-column
              min-width="200"
              label="操作">
              <template slot-scope="scope">
                <el-button title="编辑" v-show="optAuth.edit" type="info" icon='edit' size="mini"
                           @click.native.prevent="showDialog(4, scope.row)"></el-button>
                <el-button title="删除" v-show="optAuth.del" type="danger" icon='delete' size="mini"
                           @click.native.prevent="deleteItem(scope.row)"></el-button>
              </template>
            </el-table-column>
          </el-table>
        </template>
      </el-table-column>
      <el-table-column type="index" width="80">
      </el-table-column>
      <el-table-column
        prop="code"
        label="字典组代码"
        min-width="200">
      </el-table-column>
      <el-table-column
        prop="name"
        label="字典组名称"
        min-width="400">
      </el-table-column>
      <el-table-column
        prop="status"
        label="状态"
        :formatter="status"
        min-width="200">
      </el-table-column>
      <el-table-column
        min-width="150"
        label="操作">
        <template slot-scope="scope">
          <el-button title="编辑" v-show="optAuth.edit" type="info" icon='edit' size="mini"
                     @click.native.prevent="showDialog(3, scope.row)"></el-button>
          <el-button title="删除" v-show="optAuth.del" type="danger" icon='delete' size="mini"
                     @click.native.prevent="deleteItem(scope.row)"></el-button>
          <el-button title="新增字典" v-show="optAuth.add" type="primary" icon='plus' size="mini"
                     @click.native.prevent="showDialog(2)"></el-button>
        </template>
      </el-table-column>
    </el-table>
    <el-pagination class="pull-right"
                   @size-change="handleSizeChange"
                   @current-change="handleCurrentChange"
                   :current-page="table.pager.pageNo"
                   :page-sizes="[20, 50, 100, 200]"
                   :page-size="table.pager.pageSize"
                   layout="total, sizes, prev, pager, next, jumper"
                   :total="table.pager.total">
    </el-pagination>

    <!-- 弹窗框 -->
    <el-dialog :title="dialog.title" v-model="dialog.show" :close-on-press-escape="false" :close-on-click-modal="false">
      <el-form ref="dictForm" :model="dialog.form" :rules="dialog.rules" label-width="120px" class="dialog-form">
        <el-row :gutter="20">
          <el-col :span="10">
            <el-form-item label="字典代码：" prop="code">
              <el-input v-model="dialog.form.code" auto-complete="off"></el-input>
            </el-form-item>
          </el-col>
          <el-col :span="14">
            <el-form-item label="字典名称：" prop="name">
              <el-input v-model="dialog.form.name" auto-complete="off"></el-input>
            </el-form-item>
          </el-col>
        </el-row>
        <el-row :gutter="20">
          <el-col :span="10">
            <el-form-item label="状态：" prop="status">
              <el-select v-model="dialog.form.status" filterable placeholder="状态">
                <el-option v-for="(val, key) in statusMap" :label="val" :value="key"
                           :key="'form-status-'+ key"></el-option>
              </el-select>
            </el-form-item>
          </el-col>
          <el-col :span="14" v-show="dialog.type === 2 || dialog.type === 4">
            <el-form-item label="排序：" prop="sort">
              <el-input-number v-model="dialog.form.sort" :min="0"></el-input-number>
            </el-form-item>
          </el-col>
        </el-row>
        <el-row :gutter="20" v-show="dialog.type === 2 || dialog.type === 4">
          <el-col :span="10">
            <el-form-item label="参数值：" prop="value">
              <el-input v-model="dialog.form.value" auto-complete="off"></el-input>
            </el-form-item>
          </el-col>
          <el-col :span="14">
            <el-form-item label="公司ID：" prop="companyId">
              <el-select v-model="dialog.form.companyId" multiple filterable placeholder="系统类别">
                <el-option v-for="(c, key) in companies" :label="c.name" :value="c.companyId"
                           :key="'form-type-'+ c.companyId"></el-option>
              </el-select>
            </el-form-item>
          </el-col>
        </el-row>
      </el-form>
      <div slot="footer" class="dialog-footer">
        <el-button type="primary" @click="submitForm">保 存</el-button>
        <el-button @click="cancelForm">取 消</el-button>
      </div>
    </el-dialog>
    <!-- /弹窗框 -->
  </div>
</template>

<script>
  import api from '../api';
  import bsmp from 'bsmp_main';
  export default {
    name: 'dictMng',
    props: {
      height: {
        type: Number
      }
    },
    data () {
      return {
        loading: true,
        search: {
          code: '',
          name: '',
          status: ''
        },
        table: {
          items: [],
          pager: {
            pageSize: 20,
            pageNo: 1,
            total: 0
          }
        },
        dialog: {
          show: false,
          type: 1, // 类型，1：新增，2：编辑
          title: '',
          form: {
            code: '',
            name: '',
            status: '0',
            sort: null,
            value: '',
            companyId: []
          },
          parent: null,
          rules: rules
        },
        statusMap: {
          0: '启动',
          1: '禁用'
        }
      };
    },
    computed: {
      optAuth() {
        return bsmp.util.mapAuth({
          search: 'sys_config_dictMng_view',
          add: 'sys_config_dictMng_add',
          edit: 'sys_config_dictMng_edit',
          del: 'sys_config_dictMng_delete',
          child: 'sys_config_dictMng_viewChild'
        });
      },
      companies() {
        return this.$store.getters.companies;
      }
    },
    mounted () {
      this.query();
    },
    methods: {
      expand (row, expanded) {
        if (expanded && this.optAuth.child) {
          this.getDictByPid(row);
        }
      },
      /**
       * 格式化有效性
       * @param row
       * @returns {string}
       */
      status (row) {
        return this.statusMap[row.status];
      },
      selectRow (row) {
        this.dialog.parent = row;
      },
      /**
       * 显示弹窗
       * */
      showDialog (type, row) {
        if (this.$refs['dictForm'] && this.$refs['dictForm'].resetFields) {
          this.$refs['dictForm'].resetFields();
        }

        this.dialog.type = type;
        if (type === 1) {
          this.dialog.title = '新增字典分组';
          this.dialog.parent = null;
        } else if (type === 2) {
          if (!this.dialog.parent) {
            this.$message({
              type: 'warning',
              message: '请选择字典分组!'
            });
            return;
          }
          this.dialog.title = '新增字典';
        } else {
          this.dialog.title = '编辑字典';
        }

        // 编辑
        if (row) {
          let form = Object.assign({}, row);
          form.status = form.status + '';
          form.companyId = form.companyId.split(',');

          this.dialog.form = form;
        } else {
          this.dialog.form = {
            code: '',
            name: '',
            status: '0',
            sort: null,
            value: '',
            companyId: []
          };
        }

        this.dialog.show = true;
      },
      submitForm () {
        this.$refs['dictForm'].validate((valid) => {
          if (valid) {
            if (this.dialog.type === 1 || this.dialog.type === 2) {
              // 新增分组
              let params = Object.assign({},
                this.dialog.form,
                {parentCode: !this.dialog.parent ? '' : this.dialog.parent.code, companyId: this.dialog.form.companyId.join(',')});
              api.addDict(params).then((res) => {
                let data = res.data;
                if (data && data.code === '0') {
                  this.$message({
                    type: 'success',
                    message: '新增字典成功!'
                  });

                  this.dialog.show = false;
                  this.query();

                  // 记录日志
                  bsmp.log({
                    component: 'sys_config',
                    type: 'add',
                    content: `[Dict] params: ${JSON.stringify(params)}`
                  });
                }
              });
            } else {
              // 修改
              let params = {
                id: this.dialog.form.id,
                code: this.dialog.form.code,
                name: this.dialog.form.name,
                status: this.dialog.form.status,
                sort: this.dialog.form.sort,
                value: this.dialog.form.value,
                companyId: this.dialog.form.companyId.join(','),
                parentCode: this.dialog.form.parentCode
              };
              api.editDict(params).then((res) => {
                let data = res.data;
                if (data && data.code === '0') {
                  this.$message({
                    type: 'success',
                    message: '修改字典成功!'
                  });

                  this.dialog.show = false;
                  this.query();

                  // 记录日志
                  bsmp.log({
                    component: 'sys_config',
                    type: 'modify',
                    content: `[Dict] params: ${JSON.stringify(params)}`
                  });
                }
              });
            }
          } else {
            return false;
          }
        });
      },
      /**
       * 删除
       * */
      deleteItem (row) {
        this.$confirm('确定要删除该条记录?', '提示', {
          confirmButtonText: '确定',
          cancelButtonText: '取消',
          type: 'warning'
        }).then(() => {
          // 删除
          api.removeDict(row.id).then((res) => {
            let data = res.data;
            if (data && data.code === '0') {
              this.$message({
                type: 'success',
                message: '删除成功!'
              });
              this.query();

              // 记录日志
              bsmp.log({
                component: 'sys_config',
                type: 'delete',
                content: `[Dict] params: ${JSON.stringify({id: row.id})}`
              });
            }
          });
        }).catch(e => {});
      },
      /**
       * 取消弹窗
       * */
      cancelForm () {
        this.dialog.show = false;
      },

      refresh () {
        this.dialog.parent = null;
        this.query();
      },
      getDictByPid (row) {
        if (!row.nodes) {
          let result = this.getDictItem(row.id);
          this.$set(result, 'loading', true);
          api.getDictByPCode(row.code).then((res) => {
            this.$set(result, 'loading', false);
            let data = res.data;
            if (data && data.code === '0') {
              this.$set(result, 'nodes', data.data);
            }
          }).catch(e => {
            this.$set(result, 'loading', false);
          });
        }
      },
      getDictItem (id) {
        let result = null, item;
        for (let i = 0, len = this.table.items.length; i < len; i++) {
          item = this.table.items[i];
          if (item.id === id) {
            result = item;
          }
        }
        return result;
      },
      /**
       * 查询
       */
      query () {
        this.loading = true;
        let params = {};
        if (this.search.code) {
          params.code = this.search.code;
        }
        if (this.search.name) {
          params.name = this.search.name;
        }
        if (this.search.status !== '') {
          params.status = this.search.status;
        }
        params = Object.assign({}, params, this.table.pager);
        api.getDictList(params).then((res) => {
          this.loading = false;
          let data = res.data;
          if (data && data.code === '0') {
            this.table.items = data.data.data.map((item) => {
              item.loading = false;
              return item;
            });

            this.table.pager = Object.assign({}, this.table.pager,
              {
                pageNo: data.data.pageNo,
                total: data.data.total
              });
          }
        }).catch(e => {
          this.loading = false;
        });
      },

      /**
       * 每页显示条数变化
       * @param val
       */
      handleSizeChange (val) {
        this.table.pager.pageSize = val;
        this.query();
      },

      /**
       * 页码变化
       * @param val
       */
      handleCurrentChange (val) {
        if (this.table.pager.pageNo) {
          this.table.pager.pageNo = val;
          this.query();
        }
      }
    }
  };

  // 表单规则
  let rules = {
    code: [
      {required: true, message: '请输入字典代码', trigger: 'blur'}
    ],
    name: [
      {required: true, message: '请输入	简体名称', trigger: 'blur'}
    ],
    type: [
      {required: true, message: '请输入	类型', trigger: 'blur'}
    ]
  };
</script>
