<template>
  <div>
    <query-panel @switchQuery="switchQuery">
      <el-form :inline="true" :model="search" class="demo-form-inline">
        <el-form-item>
          <el-input v-model="search.loginName" placeholder="登录账号"></el-input>
        </el-form-item>
        <el-form-item>
          <el-date-picker
            clearable
            v-model="search.date"
            type="daterange"
            placeholder="选择日期范围">
          </el-date-picker>
        </el-form-item>
        <el-form-item>
          <el-select clearable v-model="search.type" placeholder="操作类型">
            <el-option v-for="(val, key) in typesMap" :label="val" :value="key" :key="'type-'+ key"></el-option>
          </el-select>
        </el-form-item>
        <el-form-item>
          <el-button v-show="optAuth.search" type="primary" @click="query">查询</el-button>
        </el-form-item>
      </el-form>
    </query-panel>
    <el-table
      highlight-current-row
      v-loading.body="loading"
      :data="table.items"
      border
      :height="tableHeight"
      style="width: 100%">
      <el-table-column type="index" width="60">
      </el-table-column>
      <el-table-column
        prop="loginName"
        label="账号"
        width="200" sortable>
      </el-table-column>
      <el-table-column
        prop="component"
        label="组件"
        width="150" sortable>
      </el-table-column>
      <el-table-column
        prop="type"
        label="操作类型"
        :formatter="typeFmt"
        width="150" sortable>
      </el-table-column>
      <el-table-column
        prop="createDate"
        label="操作日期"
        :formatter="dateFmt"
        width="200" sortable>
      </el-table-column>
      <el-table-column
        :show-overflow-tooltip="true"
        prop="content"
        label="日志内容"
        width="400">
      </el-table-column>
      <el-table-column
        prop="companyId"
        label="公司编码"
        width="100">
      </el-table-column>
      <el-table-column
        :show-overflow-tooltip="true"
        prop="browser"
        label="浏览器"
        width="200">
      </el-table-column>
      <el-table-column
        :show-overflow-tooltip="true"
        prop="createIp"
        width="200"
        label="IP">
      </el-table-column>
      <el-table-column
        fixed="right"
        width="100"
        label="操作">
        <template slot-scope="scope">
          <el-button @click="showDialog(scope.$index)" size="mini" title="查看详情"><i class="icon-eye"></i></el-button>
        </template>
      </el-table-column>
    </el-table>
    <el-pagination class="pull-right"
                   @size-change="handleSizeChange"
                   @current-change="handleCurrentChange"
                   :current-page="table.pager.pageNo"
                   :page-sizes="[20, 50, 100, 200]"
                   :page-size="table.pager.pageSize"
                   layout="total, sizes, prev, pager, next, jumper"
                   :total="table.pager.total">
    </el-pagination>

    <!-- 弹窗框 -->
    <el-dialog title="日志详情" v-model="dialog.show" :close-on-press-escape="false" :close-on-click-modal="false">
      <el-form ref="logForm" label-width="100px"
               class="dialog-form">
        <el-row :gutter="20">
          <el-col :span="12">
            <el-form-item label="账号：">
              <el-input v-model="dialog.form.loginName" auto-complete="off" readonly></el-input>
            </el-form-item>
          </el-col>
          <el-col :span="12">
            <el-form-item label="组件：">
              <el-input v-model="dialog.form.component" auto-complete="off" readonly></el-input>
            </el-form-item>
          </el-col>
        </el-row>
        <el-row :gutter="20">
          <el-col :span="12">
            <el-form-item label="操作类型：">
              <el-select clearable v-model="dialog.form.type" disabled placeholder="操作类型">
                <el-option v-for="(val, key) in typesMap" :label="val" :value="key" :key="'type-'+ key"></el-option>
              </el-select>
            </el-form-item>
          </el-col>
          <el-col :span="12">
            <el-form-item label="操作日期：">
              <el-date-picker
                readonly
                v-model="dialog.form.createDate"
                type="datetime"
                placeholder="选择日期">
              </el-date-picker>
            </el-form-item>
          </el-col>
        </el-row>
        <el-row :gutter="20">
          <el-col :span="12">
            <el-form-item label="公司编码：">
              <el-input v-model="dialog.form.companyId" auto-complete="off" readonly></el-input>
            </el-form-item>
          </el-col>
          <el-col :span="12">
            <el-form-item label="IP：">
              <el-input v-model="dialog.form.createIp" auto-complete="off" readonly></el-input>
            </el-form-item>
          </el-col>
        </el-row>
        <el-row :gutter="20">
          <el-col :span="24">
            <el-form-item label="日志内容：">
              <el-input type="textarea" readonly :rows="4" v-model="dialog.form.content"></el-input>
            </el-form-item>
          </el-col>
        </el-row>
        <el-row :gutter="20">
          <el-col :span="24">
            <el-form-item label="浏览器：">
              <el-input type="textarea" readonly :rows="4" v-model="dialog.form.browser"></el-input>
            </el-form-item>
          </el-col>
        </el-row>
      </el-form>
      <div slot="footer" class="dialog-footer">
        <el-button @click="cancelForm">取 消</el-button>
      </div>
    </el-dialog>
    <!-- /弹窗框 -->
  </div>
</template>

<script>
  import api from '../api';
  import bsmp from 'bsmp_main';
  export default {
    name: 'logMng',
    props: {
      height: {
        type: Number
      }
    },
    data () {
      return {
        loading: true,
        queryOpen: true,
        search: {
          loginName: '',
          type: '',
          logLevel: null,
          date: null
        },
        table: {
          items: [],
          pager: {
            pageSize: 20,
            pageNo: 1,
            total: 0
          }
        },
        dialog: {
          show: false,
          index: -1, // 数据下标
          form: {

          }
        },
        typesMap: {
          "add": "新增",
          "delete": "删除",
          "modify": "修改",
          "search": "查询",
          "login": "登录",
          "logout": "退出",
          "auth": "授权",
          "other": "其他"
        }
      };
    },
    mounted () {
      this.query();
    },
    computed: {
      optAuth() {
        return bsmp.util.mapAuth({
          search: 'sys_config_logMng_view'
        });
      },
      tableHeight() {
        return this.queryOpen ? this.height - 130 : this.height - 81;
      }
    },
    methods: {
      switchQuery(open) {
        this.queryOpen = open;
      },
      /**
       * 类型格式化
       * */
      typeFmt (row) {
        let fmt = typeof row.type !== 'undefined' ? this.typesMap[row.type] : '';
        return fmt ? fmt : row.type;
      },
      /**
       * 日期格式化
       * */
      dateFmt (row) {
        return row.createDate ? bsmp.util.formatDate.format(new Date(row.createDate), 'yyyy-MM-dd hh:mm:ss') : '';
      },

      /**
       * 显示弹窗
       * */
      showDialog (index) {
        this.dialog.index = index;
        let form = Object.assign({}, this.table.items[index]);

        this.dialog.form = form;
        this.dialog.show = true;
      },

      /**
       * 取消弹窗
       * */
      cancelForm () {
        this.dialog.show = false;
      },

      /**
       * 查询
       */
      query () {
        let params = {};
        if (this.search.userName) {
          params.userName = this.search.userName;
        }
        if (this.search.type !== '') {
          params.type = this.search.type;
        }
        if (this.search.date) {
          params.createDateStart = this.search.date[0];
          params.createDateEnd = this.search.date[1];
        }
        if (this.search.logLevel) {
          if (!bsmp.util.isPositiveInteger(this.search.logLevel)) {
            this.$message({
              type: 'warning',
              message: '日志级别必须为整数!'
            });
            return;
          } else {
            params.logLevel = this.search.logLevel;
          }
        }

        this.loading = true;
        params = Object.assign({}, params, this.table.pager);
        api.getLogList(params).then((res) => {
          this.loading = false;
          let data = res.data;
          if (data && data.code === '0') {
            this.table.items = data.data.data;
            this.table.pager = Object.assign({}, this.table.pager,
              {
                pageNo: data.data.pageNo,
                total: data.data.total
              });
          }
        }).catch(e => {
          this.loading = false;
        });
      },

      /**
       * 每页显示条数变化
       * @param val
       */
      handleSizeChange (val) {
        this.table.pager.pageSize = val;
        this.query();
      },

      /**
       * 页码变化
       * @param val
       */
      handleCurrentChange (val) {
        if (this.table.pager.pageNo) {
          this.table.pager.pageNo = val;
          this.query();
        }
      }
    }
  };
</script>
