<template>
  <div>
    <query-panel>
      <el-form :inline="true" :model="search" class="demo-form-inline">
        <el-form-item>
          <el-input v-model="search.name" placeholder="角色名称"></el-input>
        </el-form-item>
        <el-form-item>
          <el-input v-model="search.code" placeholder="角色代码"></el-input>
        </el-form-item>
        <el-form-item>
          <el-select clearable v-model="search.status" placeholder="状态">
            <el-option v-for="(val, key) in statusMap" :label="val" :value="key"
                       :key="'search-status-'+ key"></el-option>
          </el-select>
        </el-form-item>
        <el-form-item>
          <el-button v-show="optAuth.search" type="primary" @click="query">查询</el-button>
        </el-form-item>
      </el-form>
    </query-panel>
    <el-row class="toolbar">
      <el-button v-show="optAuth.add" @click="showDialog(1)" type="primary">新增</el-button>
      <!--<el-button v-show="optAuth.batch" @click="deleteMultiple" type="danger">批量删除</el-button>-->
    </el-row>
    <el-table
      highlight-current-row
      v-loading.body="loading"
      :data="table.items"
      border
      :max-height="height - 165"
      style="width: 100%"
      @selection-change="handleSelectionChange">
      <!--<el-table-column-->
      <!--fixed-->
      <!--type="selection"-->
      <!--width="55">-->
      <!--</el-table-column>-->
      <el-table-column type="index" width="60">
      </el-table-column>
      <el-table-column
        :show-overflow-tooltip="true"
        label="角色名称"
        width="200">
        <template slot-scope="scope">
          <span v-for="i in scope.row.depth">&nbsp;</span>{{scope.row.name}}
        </template>
      </el-table-column>
      <el-table-column
        :show-overflow-tooltip="true"
        prop="code"
        label="角色代码"
        width="150" sortable>
      </el-table-column>
      <el-table-column
        :show-overflow-tooltip="true"
        prop="parentCode"
        label="父角色编码"
        width="200" sortable>
      </el-table-column>
      <el-table-column
        prop="status"
        label="状态"
        :formatter="status"
        width="100">
      </el-table-column>
      <el-table-column
        :show-overflow-tooltip="true"
        prop="createUser"
        label="创建人"
        width="150">
      </el-table-column>
      <el-table-column
        :show-overflow-tooltip="true"
        prop="createDate"
        label="创建时间"
        min-width="200"
        :formatter="dateFmt">
      </el-table-column>
      <el-table-column
        fixed="right"
        width="200"
        label="操作">
        <template slot-scope="scope">
          <el-button title="编辑" v-show="optAuth.edit" type="info" icon='edit' size="mini"
                     @click.native.prevent="showDialog(2, scope.$index)"></el-button>
          <el-button title="删除" v-show="optAuth.del" type="danger" icon='delete' size="mini"
                     @click.native.prevent="deleteItem(scope.row.id)"></el-button>
        </template>
      </el-table-column>
    </el-table>

    <!-- 弹窗框 -->
    <el-dialog :title="dialog.title" v-model="dialog.show" :close-on-press-escape="false" :close-on-click-modal="false">
      <el-form ref="roleForm" :model="dialog.form" :rules="dialog.rules" label-width="120px" class="dialog-form">
        <el-row :gutter="20">
          <el-col :span="10">
            <el-form-item label="角色编码：" prop="code">
              <el-input v-model="dialog.form.code" auto-complete="off" :disabled="dialog.type === 2"></el-input>
            </el-form-item>
          </el-col>
          <el-col :span="14">
            <el-form-item label="角色名称：" prop="name">
              <el-input v-model="dialog.form.name" auto-complete="off"></el-input>
            </el-form-item>
          </el-col>
        </el-row>
        <el-row :gutter="20">
          <el-col :span="10">
            <el-form-item label="父角色：" prop="parentCode">
              <el-select v-model="dialog.form.parentCode" clearable placeholder="父角色">
                <el-option v-for="(r, i) in roles" :value="r.code" :label="r.name"
                           :key="'form-parentCode-'+ r.code">
                  <span v-for="i in r.depth">&nbsp;</span>{{r.name}}
                </el-option>
              </el-select>
            </el-form-item>
          </el-col>
          <el-col :span="14">
            <el-form-item label="状态：" prop="status">
              <el-select v-model="dialog.form.status" filterable placeholder="状态">
                <el-option v-for="(val, key) in statusMap" :label="val" :value="key"
                           :key="'form-status-'+ key"></el-option>
              </el-select>
            </el-form-item>
          </el-col>
        </el-row>
        <el-row :gutter="20">
          <el-col :span="24">
            <el-form-item label="备注：" prop="remark">
              <el-input type="textarea" :rows="3" v-model="dialog.form.remark"></el-input>
            </el-form-item>
          </el-col>
        </el-row>
      </el-form>
      <div slot="footer" class="dialog-footer">
        <el-button type="primary" @click="submitForm">保 存</el-button>
        <el-button @click="cancelForm">取 消</el-button>
      </div>
    </el-dialog>
    <!-- /弹窗框 -->
  </div>
</template>

<script>
  import api from '../api';
  import bsmp from 'bsmp_main';
  export default {
    name: 'roleMng',
    props: {
      height: {
        type: Number
      }
    },
    data () {
      return {
        loading: true,
        search: {
          name: '',
          code: '',
          status: ''
        },
        table: {
          items: []
        },
        dialog: {
          show: false,
          type: 1, // 类型，1：新增，2：编辑
          index: -1, // 数据下标
          title: '',
          form: {
            name: '',
            code: '',
            status: '0',
            parentCode: '',
            remark: ''
          },
          rules: rules
        },
        statusMap: {
          0: '启动',
          1: '停用'
        },
        multipleSelection: [],
        roles: []
      };
    },
    mounted () {
      this.query();
    },
    computed: {
      optAuth() {
        return bsmp.util.mapAuth({
          search: 'sys_config_roleMng_view',
          add: 'sys_config_roleMng_add',
          edit: 'sys_config_roleMng_edit',
          del: 'sys_config_roleMng_delete',
          batch: 'sys_config_roleMng_batch'
        });
      },
      company() {
        return this.$store.getters.company;
      }
    },
    methods: {
      /**
       * 日期格式化
       * */
      dateFmt (row) {
        return row.createDate ? bsmp.util.formatDate.format(new Date(row.createDate), 'yyyy-MM-dd hh:mm:ss') : '';
      },
      /**
       * 格式化有效性
       * @param row
       * @returns {string}
       */
      status (row) {
        return typeof row.status !== 'undefined' ? this.statusMap[row.status] : '';
      },
      handleSelectionChange(val) {
        this.multipleSelection = val;
      },
      /**
       * 显示弹窗
       * */
      showDialog (type, index) {
        if (this.$refs['roleForm'] && this.$refs['roleForm'].resetFields) {
          this.$refs['roleForm'].resetFields();
        }

        this.dialog.title = type === 1 ? '新增' : '修改';
        this.dialog.type = type;

        // 编辑
        if (this.dialog.type === 2 && index > -1) {
          this.dialog.index = index;
          let form = Object.assign({}, this.table.items[index]);
          form.flag = form.flag + '';
          form.status = form.status + '';

          this.dialog.form = form;
        } else {
          this.dialog.index = -1;
          this.dialog.form = {
            name: '',
            code: '',
            status: '0',
            parentCode: '',
            remark: ''
          };
        }

        this.getRoles(this.dialog.form.code);
        this.dialog.show = true;
      },
      submitForm () {
        this.$refs['roleForm'].validate((valid) => {
          if (valid) {
            if (this.dialog.type === 1) {
              let params = Object.assign({}, this.dialog.form);

              let companyId = '';
              let loginUser = this.$store.getters.loginUser;
              if (loginUser && loginUser.data) {
                if (loginUser.data.companyId && loginUser.data.companyId !== 'ALL') {
                  companyId = loginUser.data.companyId;
                } else if (this.company) {
                  companyId = this.company.companyId
                }
              }
              if (!companyId) {
                this.$message({
                  type: 'warning',
                  message: '请选择公司!'
                });
                return;
              }
              params.companyId = companyId;

              // 新增
              api.addRole(params).then((res) => {
                let data = res.data;
                if (data && data.code === '0') {
                  this.$message({
                    type: 'success',
                    message: '新增成功!'
                  });

                  this.dialog.show = false;
                  this.query();

                  // 记录日志
                  bsmp.log({
                    component: 'sys_config',
                    type: 'add',
                    content: `[Role] params: ${JSON.stringify(params)}`
                  });
                }
              });
            } else {
              // 修改
              let params = {
                id: this.dialog.form.id,
                name: this.dialog.form.name,
                code: this.dialog.form.code,
                status: this.dialog.form.status,
                parentCode: this.dialog.form.parentCode,
                remark: this.dialog.form.remark
              };
              api.editRole(params).then((res) => {
                let data = res.data;
                if (data && data.code === '0') {
                  this.$message({
                    type: 'success',
                    message: '修改成功!'
                  });

                  this.dialog.show = false;
                  this.query();

                  // 记录日志
                  bsmp.log({
                    component: 'sys_config',
                    type: 'modify',
                    content: `[Role] params: ${JSON.stringify(params)}`
                  });
                }
              });
            }
          } else {
            return false;
          }
        });
      },
      deleteMultiple () {
        if (!this.multipleSelection.length) {
          this.$message({
            type: 'warning',
            message: '请选择要删除的角色!'
          });
          return;
        }
        let ids = [];
        for (let i = 0, len = this.multipleSelection.length; i < len; i++) {
          ids.push(this.multipleSelection[i].id);
        }
        this.deleteItem(ids.join(','), 2);
      },
      /**
       * 删除
       * */
      deleteItem (id, type) {
        this.$confirm('确定要删除该条记录?', '提示', {
          confirmButtonText: '确定',
          cancelButtonText: '取消',
          type: 'warning'
        }).then(() => {
          let method = null;
          if (type && type === 2) {
            method = api.removeRoles(id);
          } else {
            method = api.removeRole(id);
          }
          // 删除
          method.then((res) => {
            let data = res.data;
            if (data && data.code === '0') {
              this.$message({
                type: 'success',
                message: '删除成功!'
              });
              this.query();

              // 记录日志
              bsmp.log({
                component: 'sys_config',
                type: 'delete',
                content: `[Role] params: ${JSON.stringify({id: id})}`
              });
            }
          });
        }).catch(e => {
        });
      },
      /**
       * 取消弹窗
       * */
      cancelForm () {
        this.dialog.show = false;
      },

      /**
       * 查询
       */
      getRoles (code) {
        let params = {};
        if (code) {
          params.code = code;
          params.flag = true;
        }
        if (this.company) {
          params.companyId = this.company.companyId
        }
        params = Object.assign({}, params);
        api.getRoleList(params).then((res) => {
          let data = res.data;
          if (data && data.code === '0') {
            this.roles = data.data;
          }
        });
      },

      /**
       * 查询
       */
      query () {
        this.loading = true;
        let params = {};
        if (this.search.name) {
          params.name = this.search.name;
        }
        if (this.search.code) {
          params.code = this.search.code;
        }
        if (this.search.status !== '') {
          params.status = this.search.status;
        }
        if (this.company) {
          params.companyId = this.company.companyId
        }
        params = Object.assign({}, params);
        api.getRoleList(params).then((res) => {
          this.loading = false;
          let data = res.data;
          if (data && data.code === '0') {
            this.table.items = data.data;
          }
        }).catch(e => {
          this.loading = false;
        });
      }
    },
    watch: {
      'company': function (company) {
        if (company) {
          this.query();
        }
      }
    }
  };

  // 表单规则
  let rules = {
    name: [
      {required: true, message: '请输入角色名称', trigger: 'blur'}
    ],
    code: [
      {required: true, message: '请输入角色代码', trigger: 'blur'}
    ]
  };
</script>
