<template>
  <div>
    <query-panel>
      <el-form :inline="true" :model="search" class="demo-form-inline">
        <el-form-item>
          <el-select v-model="search.roleCode" clearable placeholder="角色">
            <el-option v-for="(r, i) in roles" :value="r.code" :label="r.name" :key="'search-role-'+ r.code">
              <span v-for="i in r.depth">&nbsp;</span>{{r.name}}
            </el-option>
          </el-select>
        </el-form-item>
        <el-form-item>
          <el-input v-model="search.loginName" placeholder="登录账号"></el-input>
        </el-form-item>
        <el-form-item>
          <el-input v-model="search.userName" placeholder="姓名"></el-input>
        </el-form-item>
        <el-form-item>
          <el-input v-model="search.email" placeholder="邮箱"></el-input>
        </el-form-item>
        <el-form-item>
          <el-button v-show="optAuth.search" type="primary" @click="query">查询</el-button>
        </el-form-item>
      </el-form>
    </query-panel>
    <el-row class="toolbar">
      <el-button v-show="optAuth.add" @click="showDialog(1)" type="primary">新增</el-button>
      <!--<el-button v-show="optAuth.batch" @click="deleteMultiple" type="danger">批量删除</el-button>-->
    </el-row>
    <el-table
      highlight-current-row
      v-loading.body="loading"
      :data="table.items"
      border
      :max-height="height - 165"
      @selection-change="handleSelectionChange">
      <!--<el-table-column-->
      <!--fixed-->
      <!--type="selection"-->
      <!--width="55">-->
      <!--</el-table-column>-->
      <el-table-column
        prop="id"
        label="ID"
        width="60">
      </el-table-column>
      <el-table-column
        prop="loginName"
        label="登录账号"
        width="200" sortable>
      </el-table-column>
      <el-table-column
        prop="userName"
        label="姓名"
        width="150" sortable>
      </el-table-column>
      <el-table-column
        prop="roleCode"
        label="角色"
        width="150" sortable>
      </el-table-column>
      <el-table-column
        prop="status"
        label="状态"
        :formatter="status"
        width="80">
      </el-table-column>
      <el-table-column
        prop="email"
        label="邮箱"
        width="200" sortable>
      </el-table-column>
      <el-table-column
        :show-overflow-tooltip="true"
        prop="createDate"
        label="创建时间"
        min-width="200"
        :formatter="dateFmt">
      </el-table-column>
      <el-table-column
        fixed="right"
        width="200"
        label="操作">
        <template slot-scope="scope">
          <el-button title="编辑" v-show="optAuth.edit" type="info" icon='edit' size="mini"
                     @click.native.prevent="showDialog(2, scope.$index)"></el-button>
          <el-button title="删除" v-show="optAuth.del" type="danger" icon='delete' size="mini"
                     @click.native.prevent="deleteItem(scope.row.id)"></el-button>
          <el-button v-show="optAuth.resetPwd" title="重置密码"
                     @click.native.prevent="showResetForm(scope.row.loginName)"
                     size="mini"><i class="icon-key"></i></el-button>
        </template>
      </el-table-column>
    </el-table>
    <el-pagination class="pull-right"
                   @size-change="handleSizeChange"
                   @current-change="handleCurrentChange"
                   :current-page="table.pager.pageNo"
                   :page-sizes="[20, 50, 100, 200]"
                   :page-size="table.pager.pageSize"
                   layout="total, sizes, prev, pager, next, jumper"
                   :total="table.pager.total">
    </el-pagination>

    <!-- 弹窗框 -->
    <el-dialog :title="dialog.title" v-model="dialog.show" :close-on-press-escape="false" :close-on-click-modal="false">
      <el-form ref="userForm" :model="dialog.form" :rules="dialog.rules" label-width="120px" class="dialog-form">
        <el-row :gutter="20">
          <el-col :span="10">
            <el-form-item label="登录账号：" prop="loginName">
              <el-input v-model="dialog.form.loginName" auto-complete="off"></el-input>
            </el-form-item>
          </el-col>
          <el-col :span="14" v-show="dialog.type === 1">
            <el-form-item label="密码：" prop="password">
              <el-input type="password" v-model="dialog.form.password" auto-complete="off"></el-input>
            </el-form-item>
          </el-col>
        </el-row>
        <el-row :gutter="20">
          <el-col :span="10">
            <el-form-item label="姓名：" prop="userName">
              <el-input v-model="dialog.form.userName" auto-complete="off"></el-input>
            </el-form-item>
          </el-col>
          <el-col :span="14">
            <el-form-item label="角色：" prop="roleCode">
              <el-select v-model="dialog.form.roleCode" filterable placeholder="角色">
                <el-option v-for="(r, key) in roles" :value="r.code" :label="r.name"
                           :key="'form-role-'+ r.code">
                  <span v-for="i in r.depth">&nbsp;</span>{{r.name}}
                </el-option>
              </el-select>
            </el-form-item>
          </el-col>
        </el-row>
        <el-row :gutter="20">
          <el-col :span="10">
            <el-form-item label="状态：" prop="status">
              <el-select v-model="dialog.form.status" filterable placeholder="状态">
                <el-option v-for="(val, key) in statusMap" :label="val" :value="key"
                           :key="'form-status-'+ key"></el-option>
              </el-select>
            </el-form-item>
          </el-col>
          <el-col :span="14">
            <el-form-item label="邮箱：" prop="email">
              <el-input v-model="dialog.form.email" auto-complete="off"></el-input>
            </el-form-item>
          </el-col>
        </el-row>
        <el-row :gutter="20">
          <el-col :span="24">
            <el-form-item label="备注：" prop="remark">
              <el-input type="textarea" :rows="3" v-model="dialog.form.remark"></el-input>
            </el-form-item>
          </el-col>
        </el-row>
      </el-form>
      <div slot="footer" class="dialog-footer">
        <el-button type="primary" @click="submitForm">保 存</el-button>
        <el-button @click="cancelForm">取 消</el-button>
      </div>
    </el-dialog>
    <!-- /弹窗框 -->

    <!-- 弹窗框 -->
    <el-dialog title="重置密码" v-model="reset.show" size="tiny" :close-on-press-escape="false" :close-on-click-modal="false">
      <el-form ref="resetForm" :model="reset.form" label-width="120px" class="dialog-form">
        <el-row :gutter="20">
          <el-col :span="24">
            <el-form-item prop="newPwd" label="重置密码：" :rules="{required: true, message: '密码不能为空', trigger: 'blur'}">
              <el-input type="password" v-model="reset.form.newPwd" auto-complete="off"></el-input>
            </el-form-item>
          </el-col>
        </el-row>
      </el-form>
      <div slot="footer" class="dialog-footer">
        <el-button @click="submitResetForm" type="primary">保 存</el-button>
        <el-button @click="reset.show = false">取 消</el-button>
      </div>
    </el-dialog>
    <!-- /弹窗框 -->
  </div>
</template>

<script>
  import api from '../api';
  import bsmp from 'bsmp_main';
  export default {
    name: 'userMng',
    props: {
      height: {
        type: Number
      }
    },
    data () {
      return {
        loading: true,
        search: {
          roleCode: '',
          loginName: '',
          userName: '',
          email: ''
        },
        table: {
          items: [],
          pager: {
            pageSize: 20,
            pageNo: 1,
            total: 0
          }
        },
        reset: {
          show: false,
          form: {
            loginName: '',
            newPwd: ''
          }
        },
        dialog: {
          show: false,
          type: 1, // 类型，1：新增，2：编辑
          index: -1, // 数据下标
          title: '',
          form: {
            loginName: '',
            password: '',
            userName: '',
            roleCode: '',
            status: '0',
            email: '',
            remark: ''
          },
          rules: rules
        },
        statusMap: {
          0: '启动',
          1: '停用'
        },
        multipleSelection: [],
        roles: []
      };
    },
    mounted () {
      this.query();
      this.getRoles()
    },
    computed: {
      optAuth() {
        return bsmp.util.mapAuth({
          search: 'sys_config_userMng_view',
          add: 'sys_config_userMng_add',
          edit: 'sys_config_userMng_edit',
          del: 'sys_config_userMng_delete',
          batch: 'sys_config_userMng_batch',
          resetPwd: 'sys_config_userMng_resetPwd'
        });
      },
      company() {
        return this.$store.getters.company;
      }
    },
    methods: {
      showResetForm(loginName) {
        if (this.$refs['resetForm'] && this.$refs['resetForm'].resetFields) {
          this.$refs['resetForm'].resetFields();
        }

        this.reset.form.loginName = loginName;
        this.reset.form.newPwd = '';
        this.reset.show = true;
      },
      submitResetForm() {
        this.$refs['resetForm'].validate((valid) => {
          if (valid) {
            api.resetPwd(this.reset.form).then((res) => {
              let data = res.data;
              if (data && data.code === '0') {
                this.$message({
                  type: 'success',
                  message: '密码重置成功!'
                });

                this.reset.show = false;
                // 记录日志
                bsmp.log({
                  component: 'sys_config',
                  type: 'resetPwd',
                  content: `[User] params: ${JSON.stringify(this.reset.form)}`
                });
              }
            });
          } else {
            return false;
          }
        });
      },
      /**
       * 日期格式化
       * */
      dateFmt (row) {
        return row.createDate ? bsmp.util.formatDate.format(new Date(row.createDate), 'yyyy-MM-dd hh:mm:ss') : '';
      },
      /**
       * 格式化有效性
       * @param row
       * @returns {string}
       */
      status (row) {
        return typeof row.status !== 'undefined' ? this.statusMap[row.status] : '';
      },
      handleSelectionChange(val) {
        this.multipleSelection = val;
      },
      getRoles () {
        let params = {};
        if (this.company) {
          params.companyId = this.company.companyId
        }
        api.getRoleList(params).then((res) => {
          let data = res.data;
          if (data && data.code === '0') {
            this.roles = data.data;
          }
        });
      },
      /**
       * 显示弹窗
       * */
      showDialog (type, index) {
        if (this.$refs['userForm'] && this.$refs['userForm'].resetFields) {
          this.$refs['userForm'].resetFields();
        }

        this.dialog.title = type === 1 ? '新增' : '修改';
        this.dialog.type = type;

        // 编辑
        if (this.dialog.type === 2 && index > -1) {
          this.dialog.index = index;
          let form = Object.assign({}, this.table.items[index]);
          form.status = form.status + '';

          this.dialog.form = form;
        } else {
          this.dialog.index = -1;
          this.dialog.form = {
            loginName: '',
            password: '',
            userName: '',
            roleCode: '',
            status: '0',
            email: '',
            remark: ''
          };
        }

        this.dialog.show = true;
      },
      submitForm () {
        this.$refs['userForm'].validate((valid) => {
          if (valid) {
            if (this.dialog.type === 1) {
              let params = Object.assign({}, this.dialog.form, {
                loginName: this.dialog.form.loginName.trim(),
                password: this.dialog.form.password.trim(),
                userName: this.dialog.form.userName.trim()
              });

              if (params.email) {
                params.email = params.email.trim();
              }

              let companyId = '';
              let loginUser = this.$store.getters.loginUser;
              if (loginUser && loginUser.data) {
                if (loginUser.data.companyId && loginUser.data.companyId !=='ALL') {
                  companyId = loginUser.data.companyId;
                } else if (this.company) {
                  companyId = this.company.companyId
                }
              }
              if (!companyId) {
                this.$message({
                  type: 'warning',
                  message: '请选择公司!'
                });
                return;
              }
              params.companyId = companyId;
              // 新增
              api.addUser(params).then((res) => {
                let data = res.data;
                if (data && data.code === '0') {
                  this.$message({
                    type: 'success',
                    message: '新增成功!'
                  });

                  this.dialog.show = false;
                  this.query();

                  // 记录日志
                  bsmp.log({
                    component: 'sys_config',
                    type: 'add',
                    content: `[User] params: ${JSON.stringify(params)}`
                  });
                }
              });
            } else {
              // 修改
              let params = {
                id: this.dialog.form.id,
                loginName: this.dialog.form.loginName,
                userName: this.dialog.form.userName,
                roleCode: this.dialog.form.roleCode,
                status: this.dialog.form.status,
                email: this.dialog.form.email,
                remark: this.dialog.form.remark
              };
              api.editUser(params).then((res) => {
                let data = res.data;
                if (data && data.code === '0') {
                  this.$message({
                    type: 'success',
                    message: '修改成功!'
                  });

                  this.dialog.show = false;
                  this.query();

                  // 记录日志
                  bsmp.log({
                    component: 'sys_config',
                    type: 'modify',
                    content: `[User] params: ${JSON.stringify(params)}`
                  });
                }
              });
            }
          } else {
            return false;
          }
        });
      },
      deleteMultiple () {
        if (!this.multipleSelection.length) {
          this.$message({
            type: 'warning',
            message: '请选择要删除的角色!'
          });
          return;
        }
        let ids = [];
        for (let i = 0, len = this.multipleSelection.length; i < len; i++) {
          ids.push(this.multipleSelection[i].id);
        }
        this.deleteItem(ids.join(','), 2);
      },
      /**
       * 删除
       * */
      deleteItem (id, type) {
        this.$confirm('确定要删除该条记录?', '提示', {
          confirmButtonText: '确定',
          cancelButtonText: '取消',
          type: 'warning'
        }).then(() => {
          let method = null;
          if (type && type === 2) {
            method = api.removeUsers(id);
          } else {
            method = api.removeUser(id);
          }
          // 删除
          method.then((res) => {
            let data = res.data;
            if (data && data.code === '0') {
              this.$message({
                type: 'success',
                message: '删除成功!'
              });
              this.query();

              // 记录日志
              bsmp.log({
                component: 'sys_config',
                type: 'delete',
                content: `[User] params: ${JSON.stringify({id: id})}`
              });
            }
          });
        }).catch(e => {
        });
      },
      /**
       * 取消弹窗
       * */
      cancelForm () {
        this.dialog.show = false;
      },

      /**
       * 查询
       */
      query () {
        this.loading = true;
        let params = {};
        if (this.search.roleCode) {
          params.roleCode = this.search.roleCode;
        }
        if (this.search.loginName) {
          params.loginName = this.search.loginName;
        }
        if (this.search.userName) {
          params.userName = this.search.userName;
        }
        if (this.search.email) {
          params.email = this.search.email;
        }
        if (this.company) {
          params.companyId = this.company.companyId
        }
        params = Object.assign({}, params, this.table.pager);
        api.getUserList(params).then((res) => {
          this.loading = false;
          let data = res.data;
          if (data && data.code === '0') {
            this.table.items = data.data.data;
            this.table.pager = Object.assign({}, this.table.pager,
              {
                pageNo: data.data.pageNo,
                total: data.data.total
              });
          }
        }).catch(e => {
          this.loading = false;
        });
      },

      /**
       * 每页显示条数变化
       * @param val
       */
      handleSizeChange (val) {
        this.table.pager.pageSize = val;
        this.query();
      },

      /**
       * 页码变化
       * @param val
       */
      handleCurrentChange (val) {
        if (this.table.pager.pageNo) {
          this.table.pager.pageNo = val;
          this.query();
        }
      }
    },
    watch: {
      'company': function (company) {
        if (company) {
          this.search.roleCode = '';
          this.query();
          this.getRoles();
        }
      }
    }
  };

  // 表单规则
  let rules = {
    loginName: [
      {required: true, message: '请输入登录账号', trigger: 'blur'}
    ],
    userName: [
      {required: true, message: '请输入姓名', trigger: 'blur'}
    ],
    roleCode: [
      {required: true, message: '请选择角色', trigger: 'change'}
    ],
    email: [
      {type: 'email', message: '请输入正确的邮箱地址', trigger: 'blur,change'}
    ]
  };
</script>
